package 데이터출력;

public class CompOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println((3 == 4)+ " " );
		System.out.println((3 != 4)+ " " );
		System.out.println((3 > 4)+ " " );
		System.out.println((4 > 3)+ " " );

		System.out.println((3 == 3 && 4 == 7)+ " " );
		System.out.println((3 == 3 || 4 == 7)+ " " );
	}

}
