package 데이터출력;

public class PP4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("안녕하세요. 저는 %s입니다.\n", "JAVA"); // %s에 “JAVA”를 출력
		System.out.printf("저는 %d살 입니다. \n", 20); // %d에 20을 출력
		System.out.printf("정수 : %d, 실수 : %f \n", 10, 13.5); 
		// %d에 10, %f에 13.5 출력
		System.out.printf("문자 : %c, 문자열 : %s \n", 'Z', "KING"); 
		// %c에 문자 Z, %s에 “KING” 문자열을 출력

	}

}
